import { useState } from 'react';
import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useSelector } from 'react-redux';


const Cart = (props) => {
  const cartItems = useSelector(state => state.cart.items);
  const [isLoading, setIsLoading] = useState(false);
  const [carts, setCarts] = useState([]);

  const fetchCartsHandler = async () => {
    setIsLoading(true)
    const response = await window.fetch('https://react-http-7e672-default-rtdb.firebaseio.com/products');
    if (!response.ok) {
      throw new Error('something went wrong');
    }
    const responseData = await response.json();
    setCarts(responseData)
  }

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {
          cartItems.length === 0
          &&
          <h2 style={{ color: 'red' }}>Emphty!</h2>
        }
        {carts.map(cart => (
          <CartItem
            id={cart.id}
            key={cart.id}
            item={{ title: cart.title, quantity: cart.quantity, total: cart.totalPrice, price: cart.price }}
          />
        ))}
      </ul>
    </Card>
  );
};

export default Cart;
